    // 移動パターンの分析
    const movementPrediction = await this.predictUserMovement(user);

    // 予測経路に沿った気象情報
    const routeWeather: RouteWeatherInfo[] = [];

    for (const waypoint of movementPrediction.predictedRoute) {
